import styled from "@emotion/styled";

export const Layout = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  min-height: 100vh;
  flex-wrap: wrap;
`;

export const Content = styled.div`
  width: 100%;
  box-sizing: border-box;
  height: 100%;
`;
