import styled from "@emotion/styled";

export const HeaderWrapper = styled.header`
  width: 100%;
  height: 90px;
  align-items: center;
`;

export const GridLayout = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
  > * {
    &:last-child {
      grid-column-start: 2;
      grid-row-start: 1;
      grid-row-end: span 2;
      background-color: red;
    }
  }
`;
