import styled from "@emotion/styled";

export const CardContainer = styled.div`
  width: 300px;
  height: 400px;
  border-radius: 10px;
  box-shadow: rgba(99, 99, 99, 0.2) 0px 2px 8px 0px;
  background-color: #fff;
`;

export const CardContent = styled.div`
  display: flex;

  p {
    color: black;
  }
`;
