import styled from "@emotion/styled";

export const HeaderWrapper = styled.header`
  width: 100%;
  height: 90px;
  align-items: center;
`;

export const GridLayout = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1.5rem;

  &:last-child {
    background-color: red;
  }
`;
